import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Semesters() {
  const teacherId = localStorage.getItem("teacherId");  // Get teacher ID from localStorage
  const [semesters, setSemesters] = useState([]);

  useEffect(() => {
    // If teacherId exists, fetch the semesters data
    if (teacherId) {
      axios.get(`http://localhost:8080/api/semesters?teacherId=${teacherId}`)
        .then(response => {
          setSemesters(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the semesters!", error);
        });
    }
  }, [teacherId]);  // Only re-fetch if teacherId changes

  return (
    <div>
      <h2>Semesters</h2>
      <ul>
        {semesters.length > 0 ? (
          semesters.map(semester => (
            <li key={semester.id}>{semester.name}</li>
          ))
        ) : (
          <li>No semesters found</li>
        )}
      </ul>
    </div>
  );
}

export default Semesters;
