import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const [showAddForm, setShowAddForm] = useState(false);
const [newSemesterName, setNewSemesterName] = useState("");

const handleAddSemester = async () => {
  const teacherId = localStorage.getItem("teacherId");
  if (!newSemesterName.trim()) return;
  // Call backend to create semester and assign to teacher
  await axios.post("http://localhost:8080/api/semesters", {
    name: newSemesterName,
    teacherId
  });
  setShowAddForm(false);
  setNewSemesterName("");
  // Reload semesters
  loadSemesters();
};


function Semesters() {
  const [semesters, setSemesters] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const teacherId = localStorage.getItem("teacherId");
    
    if (!teacherId) {
      setError("Please login first");
      return;
    }

    axios.get(`http://localhost:8080/api/semesters?teacherId=${teacherId}`)
      .then(response => {
        setSemesters(response.data);
      })
      .catch(err => {
        console.error("Error loading semesters:", err);
        setError("Failed to load semesters");
      });
  }, []);

  const handleSemesterClick = (semesterId) => {
    navigate(`/semesters/${semesterId}/subjects`);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2>Your Semesters</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', 
        gap: '20px',
        marginTop: '20px'
      }}>
        {semesters.map((semester) => (
          <div 
            key={semester.id}
            onClick={() => handleSemesterClick(semester.id)}
            style={{
              padding: '20px',
              border: '1px solid #e2e8f0',
              borderRadius: '10px',
              cursor: 'pointer',
              transition: 'transform 0.2s',
              ':hover': {
                transform: 'translateY(-3px)',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
              }
            }}
          >
            <h3 style={{ margin: '0 0 10px 0' }}>{semester.name}</h3>
            <p style={{ color: '#64748b' }}>Click to view subjects</p>
          </div>
        ))}
      </div>

      <button 
        onClick={() => navigate('/semesters/new')}
        style={{
          marginTop: '30px',
          padding: '12px 24px',
          backgroundColor: '#2563eb',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          fontSize: '16px'
        }}
      >
        + Add New Semester
      </button>
    </div>
  );
}

export default Semesters;
