import React from 'react';
import { Link } from 'react-router-dom';

function Dashboard() {
  const teacherName = localStorage.getItem("teacherName");

  return (
    <div>
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px 20px',
        backgroundColor: '#1e293b', // Dark blue-gray
        color: 'white'
      }}>
        <div style={{ fontSize: '22px', fontWeight: 'bold' }}>
          Teacher Dashboard
        </div>
        <div>
          <Link to="/" style={{ marginRight: '15px', color: 'white', textDecoration: 'none' }}>Home</Link>
          <Link to="/semesters" style={{ marginRight: '15px', color: 'white', textDecoration: 'none' }}>Semesters</Link>
          <Link to="/profile" style={{ marginRight: '15px', color: 'white', textDecoration: 'none' }}>Profile</Link>
          <Link to="/logout" style={{ color: 'white', textDecoration: 'none' }}>Logout</Link>
        </div>
      </nav>

      <div style={{ padding: '40px', textAlign: 'center' }}>
        <div style={{
          padding: '30px',
          border: '1px solid #e2e8f0',
          borderRadius: '12px',
          maxWidth: '400px',
          margin: 'auto',
          backgroundColor: '#f8fafc',
          boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)'
        }}>
          <h2 style={{ marginBottom: '20px' }}>Welcome, {teacherName}!</h2>
          <Link
            to="/semesters"
            style={{
              padding: '10px 20px',
              backgroundColor: '#2563eb',
              color: 'white',
              borderRadius: '6px',
              textDecoration: 'none'
            }}
          >
            View Semesters
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
