import React from 'react';
import { Link } from 'react-router-dom';

function Dashboard() {
  const teacherName = localStorage.getItem("teacherName");

  return (
    <div>
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        padding: '10px',
        backgroundColor: '#333',
        color: 'white'
      }}>
        <div>
          <span style={{ fontSize: '20px' }}>Teacher Dashboard</span>
        </div>
        <div>
          <Link to="/" style={{ marginRight: '10px', color: 'white' }}>Home</Link>
          <Link to="/semesters" style={{ marginRight: '10px', color: 'white' }}>Semesters</Link>
          <Link to="/profile" style={{ marginRight: '10px', color: 'white' }}>Profile</Link>
          <Link to="/logout" style={{ color: 'white' }}>Logout</Link>
        </div>
      </nav>

      <div style={{ padding: '20px' }}>
        <div style={{
          padding: '20px',
          border: '1px solid #ccc',
          borderRadius: '10px',
          textAlign: 'center',
          maxWidth: '300px',
          margin: 'auto'
        }}>
          <h2>Welcome, {teacherName}!</h2>
          <Link to="/semesters">View Semesters</Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
