// Subjects.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Subjects() {
  const { semesterId } = useParams();
  const navigate = useNavigate();
  const [subjects, setSubjects] = useState([]);
  const [error, setError] = useState("");
  const [newSubject, setNewSubject] = useState({
    name: '',
    classroom: '',
    classTime: '',
    completionNote: '',
    homeworkNote: ''
  });
  const [showAddForm, setShowAddForm] = useState(false);
  const teacherId = localStorage.getItem("teacherId");

  useEffect(() => {
    if (!teacherId) {
      setError("Teacher ID not found. Please login again.");
      return;
    }

    loadSubjects();
  }, [semesterId, teacherId]);

  const loadSubjects = () => {
    axios.get(`http://localhost:8080/api/subjects?semesterId=${semesterId}&teacherId=${teacherId}`)
      .then(response => {
        setSubjects(response.data);
      })
      .catch(err => {
        console.error("Error loading subjects:", err);
        setError("Failed to load subjects");
      });
  };

  const handleAddSubject = () => {
    if (!teacherId) {
      setError("Teacher ID not found. Please login again.");
      return;
    }

    const subjectToAdd = {
      ...newSubject,
      semesterId: parseInt(semesterId),
      teacherId: parseInt(teacherId)
    };

    axios.post('http://localhost:8080/api/subjects', subjectToAdd)
      .then(response => {
        setSubjects([...subjects, response.data]);
        setNewSubject({
          name: '',
          classroom: '',
          classTime: '',
          completionNote: '',
          homeworkNote: ''
        });
        setShowAddForm(false);
      })
      .catch(err => {
        console.error("Error adding subject:", err);
        setError("Failed to add subject");
      });
  };

  const handleDeleteSubject = (id) => {
    axios.delete(`http://localhost:8080/api/subjects/${id}`)
      .then(() => {
        const updatedSubjects = subjects.filter(subject => subject.id !== id);
        setSubjects(updatedSubjects);
        
        if (updatedSubjects.length === 0) {
          navigate('/semesters');
        }
      })
      .catch(err => {
        console.error("Error deleting subject:", err);
        setError("Failed to delete subject");
      });
  };

  const handleUpdateSubject = (id, updatedSubject) => {
    axios.put(`http://localhost:8080/api/subjects/${id}`, updatedSubject)
      .then(response => {
        setSubjects(subjects.map(subject => 
          subject.id === id ? response.data : subject
        ));
      })
      .catch(err => {
        console.error("Error updating subject:", err);
        setError("Failed to update subject");
      });
  };

  // Rest of the component remains the same...
  // Keep the JSX structure from previous implementation

  return (
    <div style={{ padding: '20px' }}>
      <button 
        onClick={() => navigate('/semesters')}
        style={{
          marginBottom: '20px',
          padding: '8px 16px',
          backgroundColor: '#e2e8f0',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        Back to Semesters
      </button>

      <h2>Subjects</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Add Subject button and form */}
      {!showAddForm && (
        <button 
          onClick={() => setShowAddForm(true)}
          style={{
            marginBottom: '20px',
            padding: '10px 20px',
            backgroundColor: '#2563eb',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer'
          }}
        >
          Add Subject
        </button>
      )}

      {showAddForm && (
        <div style={{
          padding: '20px',
          marginBottom: '20px',
          border: '1px solid #e2e8f0',
          borderRadius: '8px',
          backgroundColor: '#f8fafc'
        }}>
          <h3>Add New Subject</h3>
          {/* Form fields */}
          <div style={{ marginBottom: '10px' }}>
            <label>Subject Name:</label>
            <input
              type="text"
              value={newSubject.name}
              onChange={(e) => setNewSubject({...newSubject, name: e.target.value})}
              style={{ marginLeft: '10px', padding: '5px' }}
            />
          </div>
          {/* Other form fields */}
          <button 
            onClick={handleAddSubject}
            style={{
              marginRight: '10px',
              padding: '8px 16px',
              backgroundColor: '#2563eb',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Save
          </button>
          <button 
            onClick={() => setShowAddForm(false)}
            style={{
              padding: '8px 16px',
              backgroundColor: '#e2e8f0',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Cancel
          </button>
        </div>
      )}

      {/* Subjects list */}
      {subjects.length > 0 ? (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
          {subjects.map((subject) => (
            <SubjectCard 
              key={subject.id}
              subject={subject}
              onDelete={handleDeleteSubject}
              onUpdate={handleUpdateSubject}
            />
          ))}
        </div>
      ) : (
        <p>No subjects found for this semester.</p>
      )}
    </div>
  );
}

// SubjectCard component remains the same
function SubjectCard({ subject, onDelete, onUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedSubject, setEditedSubject] = useState({ ...subject });

  const handleSave = () => {
    onUpdate(subject.id, editedSubject);
    setIsEditing(false);
  };

  return (
    <div style={{
      padding: '20px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      backgroundColor: '#ffffff',
      boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)'
    }}>
      {isEditing ? (
        <div>
          {/* Edit form fields */}
          <div style={{ marginBottom: '10px' }}>
            <label>Subject Name:</label>
            <input
              type="text"
              value={editedSubject.name}
              onChange={(e) => setEditedSubject({...editedSubject, name: e.target.value})}
              style={{ marginLeft: '10px', padding: '5px', width: '100%' }}
            />
          </div>
          {/* Other editable fields */}
          <button 
            onClick={handleSave}
            style={{
              marginRight: '10px',
              padding: '8px 16px',
              backgroundColor: '#2563eb',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Save
          </button>
          <button 
            onClick={() => setIsEditing(false)}
            style={{
              padding: '8px 16px',
              backgroundColor: '#e2e8f0',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Cancel
          </button>
        </div>
      ) : (
        <div>
          <h3>{subject.name}</h3>
          <p><strong>Classroom:</strong> {subject.classroom}</p>
          <p><strong>Class Time:</strong> {subject.classTime}</p>
          <p><strong>Completion Note:</strong> {subject.completionNote}</p>
          <p><strong>Homework Note:</strong> {subject.homeworkNote}</p>
          <div style={{ marginTop: '15px' }}>
            <button 
              onClick={() => setIsEditing(true)}
              style={{
                marginRight: '10px',
                padding: '6px 12px',
                backgroundColor: '#e2e8f0',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Edit
            </button>
            <button 
              onClick={() => onDelete(subject.id)}
              style={{
                padding: '6px 12px',
                backgroundColor: '#ef4444',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Delete
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Subjects;
