import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Semesters() {
  const [semesters, setSemesters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const teacherId = localStorage.getItem("teacherId");

  useEffect(() => {
    const fetchSemesters = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/teacher/semesters?teacherId=${teacherId}`);
        setSemesters(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to load semesters.");
        setLoading(false);
      }
    };

    fetchSemesters();
  }, [teacherId]);

  if (loading) return <div style={{ padding: '40px', textAlign: 'center' }}>Loading...</div>;
  if (error) return <div style={{ padding: '40px', textAlign: 'center', color: 'red' }}>{error}</div>;

  return (
    <div style={{ padding: '40px', maxWidth: '600px', margin: 'auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Your Semesters</h2>
      {semesters.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No semesters assigned yet.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {semesters.map((semester) => (
            <li
              key={semester.id}
              style={{
                backgroundColor: '#f1f5f9',
                padding: '15px 20px',
                marginBottom: '10px',
                borderRadius: '8px',
                boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
              }}
            >
              {semester.name}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Semesters;
